import React, {useState, useEffect}from 'react';
import VotesSection from '../pages/VotesSection';

const ArticlesList = () => {

    const [articles, setArticles] = useState({votes:0});
    useEffect(()=> {
        console.log("hey", new Date());
        const getArticles = async() => {
            const result = await fetch(`/api/articles-main/all`);
            const res = await result.json()
            console.log(res);
            setArticles(res);    
        }
        getArticles()
    }, )

    function mySort(arr) {
        arr.sort(function comp(a,b){return b.votes - a.votes})
    }
    
    if(articles && articles.length > 0) {
        mySort(articles);
    }
    return(
        <React.Fragment>
            <h1>Pages</h1>
            {(() => {
                console.log("heeeeeeeeey");
                return articles && articles.length && articles.map((art, key) => (
                <div key={key}>
                    <a href={"/article/" + art.name}>{art.name}</a> <br/>
                    <VotesSection articleName={art.name} votes={art.votes} updateFunc={setArticles}></VotesSection>
                </div>
            ))})()}       
        </React.Fragment>
    )
}

export default ArticlesList;
