        const result = await fetch(`/api/add-article`,
            {
                method: 'post',
                body: JSON.stringify({name, text}),
                headers:{'Content-Type':'application/json',}

            });
        const body = await result.json();
        updateFunc(body);
        setText('');
        setTitle('');

    };

    return(
        <React.Fragment>
            <label>Title:</label>
            <input type="text" value={name} onChange={(event) => setTitle(event.target.value)}/>
            <label>Content:</label>
            <textarea rows="200" cols="100" value={text} onChange={(event) => setText(event.target.value)}/>
        </React.Fragment>
    )
}

