import React from 'react';
import UpIcon from '../icons/upvote.png';
import DownIcon from '../icons/downvote.png';
const votesSection = ({articleName, votes, updateFunc}) => {
    const upVote = async () => {
        const result = await fetch(
            `/api/articles/${articleName}/upvote`,
            {method:'post',}
        );
        const body = await result.json();
        updateFunc(body);
    }
    const downVote = async () => {
        const result = await fetch (
                `/api/articles/${articleName}/downvote`,
                {method:'post',}
        );
        const body = await result.json();
        updateFunc(body);

    }
    return (
        <React.Fragment>
            <img src={UpIcon} onClick={()=> upVote()} alt="upvote button" height="20" width="50"/>
            <img src={DownIcon} onClick={()=> downVote()} alt="downvote button" height="20" width="50"/>
            <p>This post has been upvoted {votes} times</p>
        </React.Fragment>        
    )
}
export default votesSection;

