import React from 'react';
const ArticlesList = () => {
    const getArticles = async() => {
        const result = await fetch(
            `/api/articles/all`,
            {method:'post',}
        );
        return result.json();
    }

    const articles = getArticles();

    return(
        <React.Fragment>
            <h1>Pages</h1>
            {
                articles.map( article => (
                    <div>
                        <h3>{article.name}</h3>
                        <h5>{article.votes}</h5>    
                    </div>
                ))
            }
        </React.Fragment>
    )
}

export default ArticlesList;
