sourcesContent:
import React from 'react';

const votesSection = ({articleName, votes, updateFunc}) => {
    const upVote = async () => {
        const result = await fetch(
            `/api/articles/${articleName}/upvote`,
            {method:'post',}
        );
        const body = await result.json();
        updateFunc(body);
    }
    const downVote = async () => {
        const result = await fetch (
                `/api/articles/${articleName}/downvote`,
                {method:'post',}
        );
        const body = await result.json();
        updateFunc(body);

    }
    return (
        <React.Fragment>
            <img src="../icons/upvote.png" onClick={()=> upVote()} alt="upvote button"/>
            <img src="../icons/downvote.png" onClick={()=> downVote()} alt="downvote button"/>
           <button onClick={() => upVote()}>Upvote</button>
            <button onClick={() => downVote()}>Downvote</button>
            <p>This post has been upvoted {votes} times</p>
        </React.Fragment>        
    )
}
export default votesSection;

